  } else {
    // Si son diferentes, marca el estado como "different" y guarda el objeto actual formateado
    $input.all()[0].json.github_status = "different";
    $input.all()[0].json.n8n_data_stringy = JSON.stringify(orderedActual, null, 2);
    // Se detectaron diferencias entre el workflow original y el actual
  }
  // Guarda el objeto original decodificado
  $input.all()[0].json.content_decoded = orderedOriginal;

} else {
  // Si no hay archivo, se considera workflow nuevo
  const n8nWorkflow = $input.all()[1].json;
  const orderedActual = orderJsonKeys(n8nWorkflow);

  // Marca el estado como "new" y guarda el objeto actual formateado
  $input.all()[0].json.github_status = "new";
  $input.all()[0].json.n8n_data_stringy = JSON.stringify(orderedActual, null, 2);
  // No existe workflow previo, se trata de uno nuevo
}

